import React, { useCallback, useState } from 'react';
import ContainerList from './components/ContainerList/containerList';
import Modal from './components/Modal/modal';
import CatalogMap from './utils/catalogMap';
import './styles/index.scss';

const catalogMap = [
  {
    link: 'https://br.web.img2.acsta.net/c_310_420/pictures/20/06/23/20/57/4275033.jpg',
    title: 'Mulher-Maravilha 1984',
    description: 'Na década de 1980, a Mulher-Maravilha enfrenta dois novos inimigos: Max Lord e Mulher-Leopardo',
  },
  {
    link: 'https://br.web.img3.acsta.net/c_310_420/pictures/20/11/23/17/17/0659074.jpg',
    title: 'Legado Explosivo',
    description: 'Tom Carter é um especialista em demolição e uma lenda entre os criminosos. Ele conseguiu roubar uma fortuna sem nunca ter sido pego, mas tudo muda quando ele se apaixona por Annie e decide deixar o crime para trás. Disposto a mudar de vida, Tom aceita se entregar para a polícia em troca de uma sentença reduzida. Mas ele não imaginava que seria enganado por agentes corruptos do FBI, que o incriminam por um assassinato e o obrigam a fazer justiça com as próprias mãos para limpar o seu nome.',
  },
  {
    link: 'https://br.web.img2.acsta.net/c_310_420/pictures/20/10/03/00/17/1325398.jpg',
    title: 'Convenção das Bruxas',
    description: 'Em 1967, um jovem órfão vai morar com sua adorável avó na cidade de Demopolis, no interior do estado do Alabama. Lá, o garoto e sua avó se deparam com glamorosas, porém diabólicas bruxas, e ela decide fugir, levando o menino para um resort costeiro. Porém, ao chegarem lá, eles descobrem que uma grande reunião, com bruxas de todo o mundo, está acontecendo no local, e elas planejam transformar todas as crianças do mundo em ratos',
  },
  {
    link: 'https://br.web.img3.acsta.net/c_310_420/pictures/20/03/05/20/58/4942195.jpg',
    title: 'SCOOBY! O Filme',
    description: 'Scooby e sua turma encaram o seu maior e mais difícil mistério de todos os tempos: um plano maligno para liberar o cão fantasma, Cérbero, no mundo. Enquanto corre para impedir esse "apocãolipse" global, a turma descobre que Scooby tem um legado secreto e um destino épico maior do que qualquer um podia imaginar.'
  }
];

const App = () => {
  const [ movie, setMovie] = useState('');
  const [ openModal, setModal] = useState(false);

  const handleChangePis = useCallback((value) => {
    setModal(true);
    setMovie(value);
  }, []);

  const handleModal = useCallback((value) => {
    setModal(value);
  }, []);

  return (
    <>
    <section>
      <h1>Catálogo</h1>
      <ContainerList catalog={CatalogMap} change={handleChangePis} />
    </section>
    <Modal 
      title={movie.title} 
      description={movie.description} 
      change={handleModal} 
      open={openModal}
    />
    </>
  );
}

export default App;
